INSERT INTO `cauhoi_dapan` (`cau`, `nd_cauhoi`, `hinh_cauhoi`, `a`, `b`, `c`, `d`, `caudung`, `caudiemliet`, `loai_CH`, `BD_S`) VALUES
(1, 'Phần của đường bộ được sử dụng cho các phương tiện giao thông qua lại là gi?', '', '1. Phần mặt đường và lề đường', '2. Phân đường xe chạy', '3. Phần đường xe cơ giới', '', 'b', '1', '1', '7'),
(2, '“Làn đường” là gì?', '', '1. Là một phần của phần đường xe chạy được chia theo chiều dọc của đường, sử dụng cho xe chay', '2.Là một phần của phần đường xe chạy được chia theo chiều dọc của đường, có bề rộng đủ cho xe chạy an toàn', '3. Là một phần của phần đường xe chạy được chia theo chiều dọc của đường, có đủ bề rộng cho xe ô tô chạy an toàn', '', 'b', '1', '1', '10'),
(3, 'Khái niệm \"Khổ giới hạn dường bộ được hiểu như thể nào là đúng?', '', '1. Là khoảng trống có kích thước giới hạn về chiều cao, chiều rộng của đường, cầu, bến phà, hầm đường bộ để các xe kể cả hàng hóa xếp trên xe đi qua được an toàn.', '2. Là khoảng trống có kích thước giới hạn về chiều rộng của đường, cầu, bến phà, hầm trên đường bộ để các xe kể cả hàng hóa xếp trên xe đi qua được an toàn', '3. Là khoảng trống có kích thước giới hạn về chiều cao của cầu, bến phà, hầm trên đường bộ để các xe đi qua được an toàn', '', 'a', '1', '1', '2'),
(4, 'Trong các khái niệm dưới đây, \"dải phân cách” được hiểu như thế nào là đúng?', '', '1. Là bộ phận của đường để ngăn cách không cho các loại xe vào những nơi không được phép.', '2. Là bộ phận của đường để phân tách phần đường xe chạy và hành lang an toàn giao thông.', '3. Là bộ phận của đường để phân chia mặt đường thành hai chiều xe chạy riêng biệt hoặc để phân chia phần đường của xe cơ giới và xe thô sơ.', '', 'c', '1', '1', '4'),
(5, '“Dải phân cách” trên đường bộ gồm những loại nào?', '', '1. Dải phân cách gồm loại cố định và loại di động.', '2. Dải phân cách gồm tường chống ồn, hộ lan cứng và hộ lan mềm..', '3. Dải phân cách gồm giá long môn và biển báo hiệu đường bộ.', '', 'a', '1', '1', '9'),
(6, 'Người lái xe được hiểu như thế nào trong các khái niệm dưới đây?', '', '1. Là người điều khiển xe cơ giới', '2. Là người điều khiến xe thô sơ.', '3. Là người điều khiển xe có súc vật kéo.', '', 'a', '1', '1', '6'),
(7, 'Đường mà trên đó phương tiện tham gia giao thông được các phương tiện giao thông đến từ hướng khác nhường đường khi qua nơi đường giao nhau, được cảm biển báo hiệu đường ưu tiên là loại đường gì?', '', '1. Đường không ưu tiên.', '2. Đường tỉnh lộ.', '3. Đường quốc lộ.', '4. Đường ưu tiên.', 'd', '1', '1', '8'),
(8, 'Khái niệm “phương tiện giao thông cơ giới đường bộ” được hiểu thế nào là đúng?', '', '1. Gồm xe ô tô; máy kéo; xe mô tô hai bánh; xe mô tô ba bánh; xe gắn máy; xe cơ giới dùng cho người khuyết tật và xe máy chuyên dùng.', '2. Gồm xe ô tô; máy kéo; rơ moóc hoặc sơ mi rơ moóc được kéo bởi xe ô tô, máy kéo; xe mô tô hai bánh; xe mô tô ba bánh, xe gắn máy (kể cả xe máy điện) và các loại xe tương tự.', '', '', 'b', '1', '1', '4'),
(9, 'Khái niệm “phương tiện giao thông thô sơ đường bộ” được hiểu thế nào là đúng?', '', '1. Gồm xe đạp (kể cả xe đạp máy, xe đạp điện), xe xích lô, xe lăn dùng cho người khuyết tật, xe súc vật kéo và các loại xe tương tự.', '2. Gồm xe đạp (kể cả xe đạp máy, xe đạp điện), xe gắn máy, xe cơ giới dùng cho người khuyết tật và xe máy chuyên dùng.', '3. Gồm xe ô tô, máy kéo, rơ moóc hoặc sơ mỉ rơ moóc được kéo bởi xe ô tô, máy kéo.', '', 'a', '1', '1', '9'),
(10, '“Phương tiện tham gia giao thông đường bộ” gồm những loại nào?', '', '1. Phương tiện giao thông cơ giới đường bộ.', '2. Phương tiện giao thông thô sơ đường bộ và xe máy chuyên dùng.', '3.cả ý 1 và ý 2.', '', 'c', '1', '1', '8'),
(11, 'Hành vi nào dưới đây bị nghiêm cấm?', '', '1. Đỗ xe trên đường phố', '2. Sử dụng xe đạp đi trên các tuyến quốc lộ có tốc độ cao.', '3. Làm hỏng (cố ý) cọc tiêu, gương cầu, dải\nphân cách.', '4. Sử dụng còi và quay đầu xe trong khu dân cư.', 'c', '1', '1', '9'),
(12, 'Hành vi đưa xe cơ giới, xe máy chuyên dùng không bảo đảm tiêu chuẩn an toàn kỹ thuật và bảo vệ môi trường vào tham gia giao thông đường bộ có bị nghiêm cấm hay không?', '', '1. Không nghiêm cấm.', '2. Bị nghiêm cấm.', '3. Bị nghiêm cấm tùy theo các tuyến đường.', '4. Bị nghiêm cấm tuỳ theo loại xe.', 'b', '1', '1', '5'),
(13, 'Cuộc đua xe chỉ được thực hiện khi nào?', '', '1. Diễn ra trên đường phố không có người qua lại.', '2. Được người dân ủng hộ.', '3. Được cơ quan có thẩm quyền cấp phép.', '', 'c', '1', '1', '10'),
(14, 'Người điều khiển phương tiện giao thông đường bộ mà trong cơ thể có chất ma túy có bị nghiêm cấm hay không?', '', '1. Bị nghiêm cấm.', '2. Không bị nghiêm cấm.', '3. Không bị nghiêm cấm, nếu có chất ma túy ở mức nhẹ, có thể điều khiển phương tiện tham gia giao thông.', '', 'a', '1', '1', '1'),
(15, 'Việc lái xe mô tô, ô tô, máy kéo ngay sau khi uống rượu, bia có được phép hay không?', '', '1. Không được phép.', '2. Chỉ được lái ở tốc độ chậm và quãng đường ngắn.', '3. Chỉ được lái nếu trong cơ thể có nồng độ cồn thấp.', '', 'a', '1', '1', '4'),
(16, 'Người điều khiển xe ô tô, mô tô, máy kéo trên đường mà trong máu hoặc hơi thở có nồng độ cồn có bị nghiêm cấm không?', '', '1. Bị nghiêm cấm.', '2. Không bị nghiêm cấm.', '3. Không bị nghiêm cấm, nếu nồng độ cồn trong máu ở mức nhẹ, có thể điều khiển phương tiện tham gia giao thông.', '', 'a', '1', '1', '10'),
(17, 'Sử dụng rượu, bia khi lái xe, nếu bị phát hiện thì bị xử lý như thế nào?', '', '1. Chỉ bị nhắc nhở.', '2. Bị xử phạt hành chính hoặc có thể bị xử lý hình sự tùy theo mức độ vi phạm.', '3. Không bị xử lý hình sự.', '', 'b', '1', '1', '8'),
(18, 'Theo Luật phòng chống tác hại của rượu, bia, đối tượng nào dưới đây bị cấm sử dụng rượu, bia khi tham gia giao thông?', '', '1. Người điều khiển: Xe ô tô, xe mô tô, xe đạp, xe gắn máy.', '2. Người ngồi phía sau người điều khiển xe cơ giới.', '3. Người đi bộ.', '4.Cả ý 1 và ý 2.', 'a', '1', '1', '1'),
(19, 'Hành vi giao xe cơ giới, xe máy chuyên dùng cho người không đủ điều kiện để điều khiển xe tham gia giao thông có được phép hay không?', '', '1. Chỉ được thực hiện nếu đã hướng dẫn đầy đủ.', '2. Không được phép.', '3. Được phép tùy từng trường hợp.', '4. Chỉ được phép thực hiện với thành viên trong gia đình.', 'b', '1', '1', '2'),
(20, 'Hành vi điều khiển xe cơ giới chạy quá tốc độ quy định, giành đường, vượt ẩu có bị nghiêm cấm hay không?', '', '1. Bị nghiêm cấm tùy từng trường hợp.', '2. Không bị nghiêm cấm.', '3. Bị nghiêm cấm.', '', 'c', '1', '1', '5'),
(21, 'Khi lái xe trên đường, người lái xe cần quan sát và bảo đảm tốc độ phương tiện như thế nào?', '', '1. Chỉ lớn hơn tốc độ tối đa cho phép khi đường vắng.', '2. Chỉ lớn hơn tốc độ tối đa cho phép vào ban đêm.', '3. Không vượt quá tốc độ cho phép.', '', 'c', '1', '1', '6'),
(22, 'Phương tiện giao thông đường bộ di chuyển với tốc độ thấp hơn phải đi như thế nào?', '', '1. Đi về phía bên trái.', '2. Đi về phía bên phải.', '3. Đi ở giữa.', '', 'b', '1', '1', '7'),
(23, 'Trên đường có nhiều làn đường, khi điêu khiển phương tiện ở tốc độ chậm bạn phải đi ở làn đường nào?', '', '1. Đi ở làn bên phải trong cùng.', '2. Đi ở làn phía bên trái.', '3. Đi ở làn giữa.', '4. Đi ở bất cứ làn nào nhưng phải bấm đèn cảnh báo nguy hiểm để báo hiệu cho các phương tiện khác.', 'a', '1', '1', '11'),
(24, 'Hành vi vượt xe tại các vị trí có tâm nhìn hạn chế, đường vòng, đầu dốc có bị nghiêm cấm hay không?', '', '1. Không bị nghiêm cấm.', '2. Không bị nghiêm cấm khi rất vội.', '3. Bị nghiêm cấm.', '4. Không bị nghiêm cấm khi khẩn cấp.', 'c', '1', '1', '9'),
(25, 'Hành vi lắp đặt, sử dụng còi, đèn không đúng thiết kế của nhà sản xuất đối với từng loại xe cơ giới có được phép hay không?', '', '1. Được phép', '2. Không được phép.', '3. Được phép tùy từng trường hợp.', '', 'b', '1', '1', '7'),
(26, 'Việc sản xuất, mua bán, sử dụng biển số xe cơ giới, xe máy chuyên dùng được quy định như thế nào trong Luật Giao thông đường bộ?', '', '1. Được phép sản xuất, sử dụng khi bị mất biển số.', '2. Được phép mua bán, sử dụng khi bị mất biển số.', '3. Nghiêm cấm sản xuất, mua bán, sử dụng trái phép.', '', 'c', '1', '1', '5'),
(27, 'Người lái xe không được vượt xe khác khi gặp trường hợp nào ghi ở dưới đây?', '', '1. Trên cầu hẹp có một làn xe. Nơi đường giao nhau, đường bộ giao nhau cùng mức với đường sắt; xe được quyền ưu tiên đang phát tín hiệu ưu tiên đi làm nhiệm vụ.', '2. Trên cầu có từ 02 làn xe trở lên; nơi đường bộ giao nhau không cùng mức với đường sắt; xe được quyền ưu tiên đang đi phía trước nhưng không phát tín hiệu ưu tiên.', '3. Trên đường có 2 làn đường được phân chia làn bằng vạch kẻ nét đứt.\n', '', 'a', '1', '1', '6'),
(28, 'Ở phần đường dành cho người đi bộ qua đường, trên cầu, đầu cầu, đường cao tốc, đường hẹp, đường dốc, tại nơi đường bộ giao nhau cùng mức với đường sắt có được quay đầu xe hay không?', '', '1. Được phép.', '2. Không được phép.', '3. Tùy từng trường hợp.', '', 'b', '1', '1', '1'),
(29, 'Người lái xe không được quay đầu xe trong các trường hợp nào dưới đây?', '', '1. Ở phần đường dành cho người đi bộ qua đường, trên cầu, đầu cầu, đường cao tốc, đường hẹp, đường dốc, tại nơi đường bộ giao nhau cùng mức với đường sắt.', '2. Ở phía trước hoặc phía sau của phần đường dành cho người đi bộ qua đường, trên đường quốc lộ, tại nơi đường bộ giao nhau không cùng mức với đường sắt.', '3.Cả ý 1 và ý 2.', '', 'a', '1', '1', '1'),
(30, 'Người điều khiển phương tiện giao thông trên đường phố có được dừng xe, đỗ xe trên miệng cống thoát nước, miệng hầm của đường điện thoại, điện cao thế, chỗ dành riêng cho xe chữa cháy lấy nước hay không?', '', '1. Được dừng xe, đỗ xe trong trường hợp cần thiết.', '2. Không được dừng xe, đỗ xe.', '3. Được dừng xe, không được đỗ xe.', '', 'b', '1', '1', '8'),
(31, 'Người điều khiển xe mô tô hai bánh, ba bánh, xe gắn máy có được phép sử dụng xe để kéo hoặc đẩy các phương tiện khác khi tham gia giao thông không?', '', '1. Được phép.', '2. Nếu phương tiện được kéo, đẩy có khối lượng nhỏ hơn phương tiện của mình.', '3. Tùy trường hợp.', '4. Không được phép.', 'd', '1', '1', '4'),
(32, 'Khi điều khiển xe mô tô hai bánh, xe mô tô ba bánh, xe gắn máy, những hành vi buông cả hai tay; sử dụng xe để kéo, đẩy xe khác, vật khác; sử dụng chân chống của xe quệt xuống đường khi xe đang chạy có được phép hay không?', '', '1. Được phép.', '2. Tùy trường hợp.', '3. Không được phép.', '', 'c', '1', '1', '3'),
(33, 'Khi điều khiển xe mô tô hai bánh, xe mô tô ba bánh, xe gắn máy, những hành vi nào không được phép?', '', '1. Buông cả hai tay; sử dụng xe để kéo, đẩy xe khác, vật khác; sử dụng chân chống của xe quệt xuống đường khi xe đang chạy.', '2. Buông một tay; sử dụng xe để chở người hoặc hàng hoá; để chân chạm xuống đất khi khởi hành.', '3. Đội mũ bảo hiểm; chạy xe đúng tốc độ quy định và chấp hành đúng quy tắc giao thông đường bộ.', '4. Chở người ngồi sau dưới 16 tuổi.', 'a', '1', '1', '6'),
(34, 'Người ngồi trên xe mô tô hai bánh, ba bánh, xe gắn máy khi tham gia giao thông có được mang, vác vật cồng kềnh hay không?', '', '1. Được mang, vác tùy trường hợp cụ thể.', '2. Không được mang, vác.', '3. Được mang, vác nhưng phải đảm bảo an toàn.', '4. Được mang vác tùy theo sức khỏe của bản thân.', 'b', '1', '1', '11'),
(35, 'Người ngồi trên xe mô tô hai bánh, xe mô tô ba bánh, xe gắn máy khi tham gia giao thông có được bám, kéo hoặc đẩy các phương tiện khác không?', '', '1. Được phép.\n', '2. Được bám trong trường hợp phương tiện của mình bị hỏng.', '3. Được kéo, đẩy trong trường hợp phương tiện khác bị hỏng.', '4. Không được phép.', 'd', '1', '1', '2'),
(36, 'Người ngồi trên xe mô tô hai bánh, xe mô tô ba bánh, xe gắn máy khi tham gia giao thông có được sử dụng ô khi trời mưa hay không?', '', '1. Được sử dụng.', '2. Chỉ người ngồi sau được sử dụng.', '3. Không được sử dụng.', '4. Được sử dụng nếu không có áo mưa.', 'c', '1', '1', '10'),
(37, 'Khi đang lên dốc người ngôi trên xe mô tô có được phép kéo theo người đang điều khiển xe đạp hay không?', '', '1. Chỉ được phép nếu cả hai đội mũ bảo hiểm.', '2. Không được phép.', '3. Chỉ được phép thực hiện trên đường thật vắng.', '4. Chỉ được phép khi người đi xe đạp đã quá mệt.', 'b', '1', '1', '3'),
(38, 'Hành vi sử dụng xe mô tô để kéo, đẩy xe mô tô khác bị hết xăng đến trạm mua xăng có được phép hay không?', '', '1. Chỉ được kéo nếu đã nhìn thấy trạm xăng.', '2. Chỉ được thực hiện trên đường vắng phương tiện cùng tham gia giao thông.', '3. Không được phép.', '', 'c', '1', '1', '5'),
(39, 'Hành vi vận chuyển đồ vật công kênh bằng xe mô tô, xe gắn máy khi tham gia giao thông có được phép hay không?', '', '1. Không được vận chuyển.\n', '2. Chỉ được vận chuyển khi đã chẳng buộc cẩn thận.\n', '3. Chỉ được vận chuyển vật cồng kềnh trên xe máy nếu khoảng cách về nhà ngắn hơn 2 km.', '', 'a', '1', '1', '3'),
(40, 'Trên đường giao thông, khi hiệu lệnh của người điều khiển giao thông trái với hiệu lệnh của đèn hoặc biển báo hiệu thì người tham gia giao thông phải chấp hành theo hiệu lệnh nào?', '', '1. Hiệu lệnh của người điều khiển giao thông.', '2. Hiệu lệnh của đèn điều khiển giao thông.', '3. Hiệu lệnh của biển báo hiệu đường bộ.', '4. Theo quyết định của người tham gia giao thông nhưng phải bảo đảm an toàn.', 'a', '1', '1', '9'),
(41, 'Người lái xe phải làm gì khi quay đầu xe trên cầu, đường ngầm hay khu vực đường bộ giao nhau cùng mức với đường sắt?', '', '1. Không được quay đầu xe.', '2. Lợi dụng chỗ rộng và phải có người làm tín hiệu sau xe để bảo đảm an toàn.', '3. Lợi dụng chỗ rộng có thể quay đầu được để quay đầu xe cho an toàn.', '', 'a', '1', '2', '3'),
(42, 'Trên đoạn đường bộ giao nhau cùng mức với đường sắt, cầu đường bộ đi chung với đường sắt thì loại phương tiện nào được quyền ưu tiên đi trước?', '', '1. Phương tiện nào bên phải không vướng.', '2. Phương tiện nào ra tín hiệu xin đường trước.', '3. Phương tiện giao thông đường sắt.', '', 'c', '1', '1', '11'),
(43, 'Người lái xe phải làm gì khi điều khiển xe vào đường cao tốc?', '', '1. Phải có tín hiệu xin vào và phải nhường đường cho xe đang chạy trên đường; khi thấy an toàn mới cho xe nhập vào dòng xe ở làn đường sát mép ngoài; nếu có làn đường tăng tốc thì phải cho xe chạy trên làn đường đó trước khi vào làn đường của đường cao tốc.', '2. Phải có tín hiệu xin vào và phải nhanh chóng vượt xe đang chạy trên đường để nhập vào dòng xe ở làn đường sát mép ngoài; nếu có làn đường tăng tốc thì phải cho xe chạy qua làn đường đó để vào làn đường của đường cao tốc.2. Phải có tín hiệu xin vào và phải nhanh chóng vượt xe đang chạy trên đường để nhập vào dòng xe ở làn đường sát mép ngoài; nếu có làn đường tăng tốc thì phải cho xe chạy qua làn đường đó để vào làn đường của đường cao tốc.', '', '', 'a', '1', '1', '7'),
(44, 'Người ngôi trên xe mô tô 2 bánh, xe gắn máy phải đội mũ bảo hiểm có cài quai đúng quy cách khi nào?', '', '1. Khi tham gia giao thông đường bộ.', '2. Chỉ khi đi trên đường chuyên dùng; đường cao tốc.', '3. Khi tham gia giao thông trên đường tỉnh lộ hoặc quốc lộ.', '', 'a', '1', '1', '3'),
(45, 'Người điều khiển xe mô tô hai bánh, xe gắn máy có được đi xe dàn hàng ngang; đi xe vào phần đường dành cho người đi bộ và phương tiện khác; sử dụng ô, điện thoại di động, thiết bị âm thanh (trừ thiết bị trợ thính) hay không?', '', '1. Được phép nhưng phải đảm bảo an toàn.', '2. Không được phép.', '3. Được phép tùy từng hoàn cảnh, điều kiện cụ thể.', '', '2', '1', '1', '1'),
(46, 'Khi gặp một đoàn xe, một đoàn xe tang hay gặp một đoàn người có tổ chức theo đội ngũ, người lái xe phải xử lý như thế nào?', '', '1. Từ từ đi cắt qua đoàn người, đoàn xe.', '2. Không được cắt ngang qua đoàn người, đoàn xe.', '3. Báo hiệu từ từ cho xe đi cắt qua để bảo đảm an toàn.', '', 'b', '1', '1', '11'),
(47, 'Khi lái xe trên đường vắng mà cảm thấy buồn ngủ, người lái xe nên chọn cách xử lý như thế nào cho phù hợp?', '', '1. Tăng tốc độ kết hợp với nghe nhạc và đi tiếp.', '2. Quan sát và dừng xe tại nơi quy định; nghỉ cho đến khi hết buồn ngủ và đi tiếp.', '3. Sử dụng một ít rượu hoặc bia để hết buồn ngủ và đi tiếp.', '', 'b', '1', '3', '4'),
(48, 'Trên đường cao tốc, người lái xe xử lý như thế nào khi đã vượt quá lối ra của đường định rẽ?', '', '1. Quay xe, chạy trên lề đường bên phải và rẽ khỏi đường cao tốc.', '2. Lùi xe sát lề đường bên phải và rẽ khỏi đường cao tốc.', '3. Tiếp tục chạy đến lối ra tiếp theo.', '', 'c', '1', '3', '11'),
(49, 'Tại các điểm giao cắt giữa đường bộ và đường sắt, quyền ưu tiên thuộc về loại phương tiện nào dưới đây?', '', '1. Xe cứu hỏa.', '2. Xe cứu thương.', '3. Phương tiện giao thông đường sắt.', '4. Ô tô, mô tô và xe máy chuyên dùng.', 'c', '1', '1', '8'),
(50, 'Trên đoạn đường bộ giao nhau đồng mức với đường sắt, người không có nhiệm vụ có được tự ý mở chắn đường ngang khi chắn đã đóng hay không?', '', '1. Không được phép.', '2. Được phép nhưng phải đảm bảo an toàn.', '3. Được phép tùy từng hoàn cảnh và điều kiện cụ thể.', '', 'a', '1', '1', '6'),
(51, 'Khi điều khiển phương tiện tham gia giao thông, những hành vi nào dưới đây bị nghiêm cấm?', '', '1. Thay đổi tốc độ của xe trên đường bộ.', '2. Thay đổi tay số của xe trên đường bộ.', '3. Lạng lách, đánh võng trên đường bộ.', '', 'c', '1', '1', '7'),
(52, 'Khi xe ô tô, mô tô đến gần vị trí giao nhau giữa đường bộ và đường sắt không có rào chắn, khi đèn tín hiệu màu đỏ đã bật sáng hoặc khi có tiếng chuông báo hiệu, người lái xe xử lý như thế nào?', '', '1. Giảm tốc độ cho xe vượt qua đường sắt.', '2. Nhanh chóng cho xe vượt qua đường sắt trước khi tàu hỏa tới.', '3. Giảm tốc độ cho xe vượt qua đường sắt trước khi tàu hỏa tới.', '4. Cho xe dừng ngay lại và giữ khoảng cách tối thiểu 5 mét tính từ ray gần nhất.', 'd', '1', '1', '2'),
(53, 'Khi muốn lùi xe nhưng không quan sát được phía sau, cần phải làm gì để đảm bảo an toàn?', '', '1. Phải lùi thật chậm.', '2. Có thể được lùi xe nhưng phải mở cửa xe.', '3. Không được lùi xe.', '4. Bấm còi 3 lần liên tiếp trước khi lùi.', 'c', '1', '1', '5'),
(54, 'Người điều khiển phương tiện tham gia giao thông đường bộ phải giảm tốc độ để có thể dừng lại một cách an toàn trong các trường hợp nào dưới đây?', '', '1. Khi có người đi bộ, xe lăn của người khuyết tật qua đường; đến gần bến xe buýt, điểm dừng đỗ xe có khách đang lên, xuống xe.', '2. Khi điều khiển phương tiện đi qua khu vực trạm kiểm soát tải trọng xe, trạm cảnh sát giao thông, trạm giao dịch thanh toán đối với các phương tiện sử dụng đường bộ.', '3.Cả ý 1và ý 2.', '', 'c', '1', '1', '10'),
(55, 'Khi xe gặp sự cố kỹ thuật trên đường cao tốc, bạn phải xử lý theo thứ tự như thế nào dưới đây để đảm bảo an toàn giao thông?', '', '1. Bật đèn tín hiệu khẩn cấp, dừng xe ngay lập tức và đặt biển báo hiệu nguy hiểm để cảnh báo cho các xe khác.', '2. Bật đèn tín hiệu khẩn cấp, lập tức đưa xe vào làn đường xe chạy bên phải trong cùng, đặt biển báo hiệu nguy hiểm để cảnh báo cho các xe khác.', '3. Bật đèn tín hiệu khẩn cấp, khi đủ điều kiện an toàn nhanh chóng đưa xe vào làn dừng đỗ khẩn cấp, đặt biển báo hiệu nguy hiểm để cảnh báo cho các xe khác', '', 'c', '1', '2', '2'),
(56, '“Người tham gia giao thông đường bộ” gồm những đối tượng nào?', '', '1. Người điều khiển, người sử dụng phương tiện tham gia giao thông đường bộ.', '2. Người điều khiển, dẫn dắt súc vật; người đi bộ trên đường bộ.', '3. Cả ý 1 và ý 2.', '', 'c', '', '1', '3'),
(57, 'Người điều khiển phương tiện tham gia giao thông đường bộ” gồm những đối tượng nào dưới đây?', '', '1. Người điều khiển xe cơ giới, người điều khiển xe thô sơ.', '2. Người điều khiển xe máy chuyên dùng tham gia giao thông đường bộ.', '3. Cả ý 1 và ý 2', '', 'c', '', '1', '5'),
(58, 'Khái niệm “người điều khiển giao thông” được hiểu như thế nào là đúng?', '', '1. Là người điều khiển phương tiện tham gia giao thông tại nơi thi công, nơi ùn tắc giao thông, ở bến phà, tại cầu đường bộ đi chung với đường sắt.', '2. Là cảnh sát giao thông, người được giao nhiệm vụ hướng dẫn giao thông tại nơi thi công, nơi ùn tắc giao thông, ở bến phà, tại cầu đường bộ đi chung với đường sắt.', '3. Là người tham gia giao thông tại nơi thi công, nơi ùn tắc giao thông, ở bến phà, tại cầu đường bộ đi chung với đường sắt.', '', 'b', '', '1', '7'),
(59, 'Trong các khái niệm dưới đây khái niệm “dừng xe” được hiểu như thế nào là đúng?', '', '1. Là trạng thái đứng yên của phương tiện giao thông không giới hạn thời gian để cho người lên, xuống phương tiện, xếp dỡ hàng hóa hoặc thực hiện công việc khác.', '2. Là trạng thái đứng yên tạm thời của phương tiện giao thông trong một khoảng thời gian cần thiẽ! đủ để cho người lên, xuống phương tiện, xếp dỡ hàng hóa hoặc thực hiện công việc khác.', '3. Là trạng thái đứng yên của phương tiện giao thông không giới hạn thời gian giữa 02 lần vận chuyển hàng hóa hoặc hành khách.', '', 'b', '', '1', '10'),
(60, 'Khái niệm “đồ xe” được hiểu như thế nào là đúng quy tắc giao thông?', '', '1. Là trạng thái đứng yên của phương tiện giao thông có giới hạn trong một khoảng thời gian cần thiết đủ để cho người lên, xuống phương tiện đó, xếp dỡ hàng hóa hoặc thực hiện công việc khác.', '2. Là trạng thái đứng yên của phương tiện giao\nthông không giới hạn thời gian.', '', '', 'b', '', '1', '10'),
(61, 'Khái niệm “đường cao tốc” được hiểu như thế nào là đúng?', '', '1. Đường dành riêng cho xe ôtô và một số loại xe chuyên dùng được phép đi vào theo quy định của Luật Giao thông đường bộ.', '2. Có dải phân cách phân chia đường cho xe chạy hai chiều riêng biệt mà dải phân cách này xe không đi được lên trên; không giao nhau cùng mức với một hoặc một số đường khác.', '3. Được bố trí đầy đủ trang thiết bị phục vụ, bảo đảm giao thông liên tục, an toàn, rút ngắn thời gian hành trình và chỉ cho xe ra, vào ở những điểm nhất định.', '4. Tất cả các ý trên.', 'd', '', '1', '11'),
(62, 'Khi lái xe trong khu đô thị và đông dân cư trừ các khu vực có biển cấm sử dụng còi, người lái xe được sử dụng còi như thế nào trong các trường hợp dưới đây?', '', '1. Từ 22 giờ đêm đến 5 giờ sáng.', '2. Từ 5 giờ sáng đến 22 giờ tối.', '3.  Từ 23 giờ đêm đến 5 giờ sáng hôm sau.', '', 'b', '', '1', '3'),
(63, 'Người lái xe sử dụng đèn như thế nào khi lái xe trong khu đô thị và đông dân cư vào ban đêm?', '', '1. Bất cứ đèn nào miễn là mắt nhìn rõ phía trước.', '2. Chỉ bật đèn chiếu xa (đèn pha) khi không nhìn rõ đường.', '3. Đèn chiếu xa (đèn pha) khi đường vắng, đèn pha chiếu gần (đèn cốt) khi có xe đi ngược\nchiều.', '4. Đèn chiếu gần (đèn cốt).', 'd', '', '1', '5'),
(64, 'Trong trường hợp đặc biệt, để được lắp đặt, sử dụng còi, đèn khác thiết kế của nhà sản xuất đối với từng loại xe cơ giới bạn phải đảm bảo yêu cầu nào dưới đây?', '', '1. Phải đảm bảo phụ tùng do đúng nhà sản xuất đó cung cấp.', '2. Phải được chấp thuận của cơ quan có thẩm quyền.', '3. Phải là xe đăng ký và hoạt động tại các khu vực có địa hình phức tạp.', '', 'b', '', '1', '1'),
(65, 'Bạn đang lái xe phía trước có một xe cảnh sát giao hông không phát tín hiệu ưu tiên bạn có được phép vượt hay không?', '', '1. Không được vượt.', '2. Được vượt khi đang đi trên cầu.', '3. Được phép vượt khi đi qua nơi giao nhau có ít phương tiện cùng tham gia giao thông.', '4. Được vượt khi đảm bảo an toàn.', 'd', '', '1', '10'),
(66, 'Bạn đang lái xe phía trước có một xe cứu thương đang phát tín hiệu ưu tiên bạn có được phép vượt hay không?', '', '1. Không được vượt.', '2. Được vượt khi đang đi trên cầu.', '3. Được phép vượt khi đi qua nơi giao nhau có ít phương tiện cùng tham gia giao thông.', '4. Được vượt khi đảm bảo an toàn.', 'a', '', '1', '8'),
(67, 'Bạn đang lái xe trong khu dân cư, có đông xe qua lại, nếu muốn quay đầu xe bạn cần làm gì để tránh ùn tắc và đảm bảo an toàn giao thông?', '', '1. Đi tiếp đến điểm giao cắt gần nhất hoặc nơi có biển báo cho phép quay đầu xe.', '2. Bấm đèn khẩn cấp và quay đầu xe từ từ bảo đảm an toàn.', '3. Bấm còi liên tục khi quay đầu xe để cảnh báo các xe khác.', '4. Nhờ một người ra hiệu giao thông trên đường chậm lại trước khi quay đầu.', 'a', '', '1', '3'),
(68, 'Người lái xe không được lùi xe ở những khu vực nào dưới đây?', '', '1. Ở khu vực cho phép đỗ xe.', '2. Ở khu vực cấm dừng và trên phần đường dành cho người đi bộ qua đường.', '3. Nơi đường bộ giao nhau, đường bộ giao nhau cùng mức với đường sắt, nơi tầm nhìn bị che khuất, trong hầm đường bộ, đường cao tốc.', '4.cả ý 2 và ý 3.', 'd', '', '1', '5'),
(69, 'Khi xe đã kéo 1 xe hoặc xe đã kéo 1 rơ moóc, bạn có được phép kéo thêm xe (kể cả xe thô sơ) hoặc rơ moóc thứ hai hay không?', '', '1. Chỉ được thực hiện trên đường quốc lộ có hai làn xe một chiều.', '2. Chỉ được thực hiện trên đường cao tốc.', '3. Không được thực hiện vào ban ngày.', '4. Không được phép.', 'd', '', '1', '1'),
(70, 'Người đủ bao nhiêu tuổi trở lên thì được điêu khiển xe ô tô tải, máy kéo có trọng tải từ 3.500 kg trở lên; xe hạng B2 kéo rơ moóc (FB2)?', '', '1.19 tuổi.', '2. 21 tuổi.', '3. 20tuổi.', '', 'b', '', '1', '4'),
(71, 'Người đủ bao nhiêu tuổi trở lên thì được điều khiển xe mô tô hai bánh, xe mô tô ba bánh có dung tích xi lanh từ 50 cm3 trở lên và các loại xe có kếtcấu tương tự; xe ô tô tải, máy kéo có trọng tải dưới 3.500 kg; xe ô tô chở người đến 9 chỗ ngồi?', '', '1.16 tuổi.', '2. 18 tuổi.', '3. 17 tuổi.', '', 'b', '', '1', '9'),
(72, 'Người lái xe ô tô chở người trên 30 chỗ ngồi (hạng E), lái xe hạng D kéo rơ moóc (FD) phải đủ bao nhiêu tuổi trở lên?', '', '1. 23 tuổi.', '2. 24 tuổi.', '3. 27 tuổi.', '4. 30 tuổi.', 'c', '', '1', '7'),
(73, 'Tuổi tối đa của người lái xe ô tô chở người trên 30 chỗ ngồi (hạng E) là bao nhiêu tuổi?', '', '1. 55 tuổi đối với nam và 50 tuổi đối với nữ.', '2. 55 tuổi đối với nam và nữ.', '3. 60 tuổi đối với nam và 55 tuổi đối với nữ.', '4. 45 tuổi đối với nam và 40 tuổi đối với nữ.', 'a', '', '1', '1'),
(74, 'Người lái xe chở người từ 10 đến 30 chỗ ngôi (hạng D), lái xe hạng C kéo rơ moóc (FC) phải đủ bao nhiêu tuổi trở lên?', '', '1.23 tuổi.', '2.24 tuổi.', '3.22 tuổi.', '4. 18 tuổi.', 'b', '', '1', '10'),
(75, 'Người đủ 16 tuổi được điều khiển các loại xe nào dưới đây?\n', '', '1 . Xe mô tô 2 bánh có dung tích xi-lanh từ 50 cm3 trở lên.', '2. Xe gắn máy có dung tích xi-lanh dưới 50 cm3.', '3. Xe ô tô tải dưới 3.500 kg; xe chở người đến 9 chỗ ngồi.', '4. Tất cả các ý nêu trên.', 'b', '', '1', '9'),
(76, 'Người có giấy phép lái xe mô tô hạng A1 không được phép điều khiển loại xe nào dưới đây?', '', '1. Xe mô tô có dung tích xi-lanh 125 cm3.', '2. Xe mô tô có dung tích xi-lanh từ 175 cm3 trở lên.', '3. Xe mô tô có dung tích xi-lanh 100 cm3.', '', 'b', '', '1', '11'),
(77, 'Người có giấy phép lái xe mô tô hạng A1 được phép điều khiển loại xe nào dưới đây?', '', 'Xe mô tô có dung tích xi-lanh từ 50 cm3 đến dưới 175 cm3.', '2. Xe mô tô ba bánh dành cho người khuyết tật.', '3. Cả ý 1và ý 2.', '', 'c', '', '1', '4'),
(78, 'Người có giấy phép lái xe mô tô hạng A2 được phép điều khiển loại xe nào dưới đây?', '', '1. Xe mô tô ba bánh.', '2. Xe mô tô hai bánh có dung tích xi lanh từ 175 cm3 trở lên và các loại xe quy định cho giấy phép lái xe hạng A1.', '3. Các loại máy kéo nhỏ có trọng tải đến 1.000 kg.', '', 'b', '', '1', '6'),
(79, 'Người có giấy phép lái xe mô tô hạng A3 được phép điều khiển loại xe nào dưới đây?', '', '1. Xe mô tô ba bánh.', '2. Xe mô tô hai bánh có dung tích xi lanh từ 175\ncm3 trở lên.', '3. Các loại máy kéo nhỏ có trọng tải đến 1.000\nkg', '', 'a', '', '1', '11'),
(80, 'Người có giấy phép lái xe hạng B1 số tự động được điều khiển loại xe nào?', '', '1. Ô tô số tự động chở người đến 9 chỗ ngồi, kể cả chỗ ngồi cho người lái xe; ô tô tải, kể cả ô tô tải chuyên dùng số tự động có trọng tải thiết kế dưới 3.500 kg; ô tô dùng cho người khuyết tật. Không được hành nghề lái xe.', '2. Ô tô số tự động chở người đến 9 chỗ ngồi, kể cả chỗ ngồi cho người lái xe; ô tô tải, kể cả ô tô tải chuyên dùng số tự động có trọng tải thiết kế dưới 3.500 kg; ô tô dùng cho người khuyết tật. Được hành nghề lái xe kinh doanh vận tải.', '3. Ô tô chở người đến 9 chỗ ngồi, kể cả chỗ ngồi cho người lái xe; ô tô tải, kể cả ô tô tải chuyên dùng có trọng tải thiết kế dưới 3.500 kg; ô tô dùng cho người khuyết tật.', '', 'a', '', '1', '2'),
(81, 'Người có giấy phép lái xe hạng B1 được điều khiển loại xe nào?', '', '1. Ô tô chở người đến 9 chỗ ngồi, kể cả chỗ ngồi cho người lái xe; ô tô tải, kể cả ô tô tải chuyên dùng có trọng tải thiết kế dưới 3.500 kg; máy kéo kéo một rơ moóc có trọng tải thiết kế dưới 3.500 kg. Được hành nghề lái xe.', '2. Ô tô chở người đến 9 chỗ ngồi, kể cả chỗ ngồi cho người lái xe; ô tô tải, kể cả ô tô tải chuyên dùng có trọng tải thiết kế dưới 3.500 kg; máy kéo kéo một rơ moóc có trọng tải thiết kế dưới 3.500 kg. Không hành nghề lái xe.', '3. Ô tô số tự động chở người đến 9 chỗ ngồi, kể cả chỗ ngồi cho người lái xe; ô tô tải, kể cả ô tô tải chuyên dùng số tự động có trọng tải thiết kế\ntrên 3.500 kg; ô tô dùng cho người khuyết tật.', '', 'b', '', '1', '3'),
(82, 'Người có giấy phép lái xe hạng B2 được điều khiển loại xe nào?', '', '1. Xe ô tô chở người trên 9 chỗ ngồi; xe ô tô tải; máy kéo có trọng tải trên 3.500 kg.', '2. Xe ô tô chở người đến 9 chỗ ngồi; xe ô tô tải; máy kéo có trọng tải dưới 3.500 kg.', '3. Xe ô tô chở người từ 10 đến 30 chỗ ngồi; xe ô tô tải; máy kéo có trọng tải trên 3.500 kg.', '', 'b', '', '1', '6'),
(83, 'Người có giấy phép lái xe hạng C được điều khiển loại xe nào?', '', '1. Xe ô tô chở người trên 9 chỗ ngồi; xe ô tô tải; máy kéo có trọng tải trên 3.500 kg.', '2. Xe ô tô chở người từ 10 đến 30 chỗ ngồi; xe ô tô tải; máy kéo có trọng tải trên 3.500 kg.', '3. Xe ô tô chở người đến 9 chỗ ngồi; xe ô tô tải; máy kéo có trọng tải trên 3.500 kg.', '', 'c', '', '1', '3'),
(84, 'Người có giấy phép lái xe hạng D được điều khiển loại xe nào dưới đây?', '', 'Xe ô tô chở người đến 30 chỗ ngồi, kể cả chỗ ngồi cho người lái xe; xe ô tô tải, máy kéo có trọng tải trên 3.500 kg.', '2. Xe ô tô chở người trên 30 chỗ ngồi; xe ô tô tải, máy kéo có trọng tải trên 3.500 kg.', '3. Xe kéo rơ moóc, ô tô đầu kéo kéo sơ mỉ rơ moóc và được điều khiển các loại xe theo quy định cho giấy phép lái xe hạng B1, B2, C và FB2.\n', '', 'a', '', '1', '7'),
(85, 'Người có giấy phép lái xe hạng E được điều khiển loại xe nào dưới đây?', '', '1. Xe kéo rơ moóc, ô tô đầu kéo kéo sơ mi rơ moóc và được điều khiển các loại xe theo quy định cho giấy phép lái xe hạng B1, B2, C và FB2.', '2. Xe ô tô chở người trên 30 chỗ ngồi; xe ô tô tải, máy kéo có trọng tải trên 3.500 kg.', '3. Xe kéo rơ moóc và được điều khiển các loại xe; ô tô chở khách nối toa và các loại xe quy định cho giấy phép lái xe hạng B1, B2, C, D, FB2, FD.', '', 'b', '', '1', '5'),
(86, 'Người có giấy phép lái xe hạng FC được điều khiến loại xe nào dưới đây?', '', '1. Các loại xe được quy định tại giấy phép lái xe hạng C có kéo rơ moóc, ô tô đầu kéo kéo sơ mi rơ moóc, ô tô chở khách nối toa và được điều khiển các loại xe quy định cho giấy phép lái xe hạng B1, B2 và FB2.', '2. Các loại xe được quy định tại giấy phép lái xe hạng C có kéo rơ moóc, ô tô đầu kéo kéo sơ mi rơ moóc và được điều khiển các loại xe quy định cho giấy phép lái xe hạng B1, B2 và FB2.', '3. Mô tô hai bánh, các loại xe được quy định tại giấy phép lái xe hạng C có kéo rơ moóc, ô tô đầu kéo kéo sơ mỉ rơ moóc và được điều khiển các loại xe quy định cho giấy phép lái xe hạng B1,B2và FB2.', '4. Tất cả các loại xe nêu trên.', 'b', '', '1', '4'),
(87, 'Biển báo hiệu có dạng hình tròn, viền đỏ, nền trắng, trên nền có hình vẽ hoặc chữ số, chữ viết màu đen là loại biển gì dưới đây?', 'https://cdn.baogiaothong.vn/upload/images/2020-2/article_img/2020-05-23/bien-bao-cam-103a-1590225067-width232height232.jpg', '1. Biển báo nguy hiểm.', '2. Biển báo cấm.', '3. Biển báo hiệu lệnh.', '4. Biển báo chỉ dẫn.', 'b', '', '1', '5'),
(88, 'Biển báo hiệu hình tròn có nên xanh lam có hình vẽ màu trắng là loại biển gì dưới đây?', 'http://bizweb.dktcdn.net/thumb/grande/100/352/036/products/bien-301-a.jpg?v=1600053811687', '1. Biển báo nguy hiểm.', '2. Biển báo cấm.', '3. Biển báo hiệu lệnh phải thi hành.', '4. Biển báo chỉ dẫn.', 'c', '', '1', '8'),
(89, 'Biển báo hiệu hình chữ nhật hoặc hình vuông hoặc hình mũi tên nền xanh lam là loại biển gì dưới đây?', 'https://daotaolaixehd.com.vn/wp-content/uploads/2016/12/bien-chi-dan-410.png', '1. Biển báo nguy hiểm.', '2. Biển báo cấm.', '3. Biển báo hiệu lệnh phải thi hành.', '4. Biển báo chỉ dẫn.', 'd', '', '1', '5'),
(90, 'Khi điều khiển xe chạy trên đường, người lái xe phải mang theo các loại giấy tờ gì?', '', '1. Giấy chứng nhận tốt nghiệp khóa đào tạo của hạng xe đang điều khiển, đăng ký xe, giấy phép lưu hành xe.', '2. Giấy phép lái xe phù hợp với loại xe đó; lệnh vận chuyển, đăng ký xe, giấy chứng nhận kiểm tra chất lượng an toàn kỹ thuật và bảo vệ môi trường của xe cơ giới sau khi cải tạo; giấy phép vận chuyển (nếu loại xe đó cần phải có).', '3. Giấy phép lái xe phù hợp với loại xe đó, đăng ký xe, giấy chứng nhận kiểm định kỹ thuật và bảo vệ môi trường, giấy chứng nhận bảo hiểm trách nhiệm dân sự của chủ xe cơ giới và giấy phép vận chuyển (nếu loại xe đó cần phải có),  các giấy tờ phải còn giá trị sử dụng.', '', 'c', '', '1', '7'),
(91, 'Khi sử dụng giấy phép lái xe đã khai báo mất để điều khiển phương tiện cơ giới đường bộ, ngoài việc bị thu hồi giấy phép lái xe, chịu trách nhiệm trước pháp luật, người lái xe không được cấp giấy phép lái xe trong thời gian bao nhiêu năm?', '', '1.02năm.', '2.03năm.', '3.05năm.', '4. 04 năm.', 'c', '', '1', '9'),
(92, 'Khi gặp hiệu lệnh như dưới đây của cảnh sát giao thông thì người tham gia giao thông phải đi như thế nào là đúng quy tắc giao thông?', 'https://lh3.googleusercontent.com/y92hgWdTjKCG5-J5sqFpdCxKNDDMK4Db3XX9rx8kfcM67hWR8XXTL24G_p0HMJntCaEQvWQ=s112', '1. Người tham gia giao thông ở các hướng phải dừng lại.', '2. Người tham gia giao thông ở các hướng được đi theo chiều gậy chỉ của cảnh sát giao thông.', '3. Người tham gia giao thông ở phía trước và phía sau người điều khiển được đi tất cả các hướng; giao thông ở phía bên phải và phía bên trái người điều khiển phải dừng lại.', '4. Người tham gia giao thông ở phía trước và phía sau người điều khiển phải dừng lại; giao thông ở phía bên phải và bên trái người điều khiển đươc đi tất cả các hướng.', 'd', '', '1', '2'),
(93, 'Khi gặp hiệu lệnh như dưới đây của cảnh sát giao thông thì người tham gia giao thông phải đi như thế nào là đúng quy tắc giao thông?', 'https://lh3.googleusercontent.com/aMM_b5VCw95ToTnO9Toyqu-OB35Gdp207RBa0-AbdVnMk3LIG8XGR8qo6cTIK_0dntqJNA=s85', '1. Người tham gia giao thông ở hướng đối diện cảnh sát giao thông được đi, các hướng khác cần phải dừng lại.', '2. Người tham gia giao thông được rẽ phải theo chiều mũi tên màu xanh ở bục cảnh sát giao thông.', '3. Người tham gia giao thông ở các hướng đều phải dừng lại trừ các xe đã ở trong khu vự giao nhau.3. Người tham gia giao thông ở các hướng đều phải dừng lại trừ các xe đã ở trong khu vự giao nhau.', '4. Người ở hướng đối diện cảnh sát giao thông phải dừng lại, các hướng khác được đi trong đó có bạn.', 'c', '', '1', '2'),
(94, 'Tại nơi có biển báo hiệu cố định lại có báo hiệu tạm thời thì người tham gia giao thông phải chấp hành hiệu lệnh của báo hiệu nào?', '', '1. Biển báo hiệu cố định.', '2. Báo hiệu tạm thời.', '', '', 'b', '', '1', '6'),
(95, 'Trên đường có nhiều làn đường cho xe đi cùng chiều được phân biệt bằng vạch kẻ phân làn đường, người điều khiển phương tiện phải cho xe đi như thế nào?', '', '1. Cho xe đi trên bất kỳ làn đường nào hoặc giữa 02 làn đường nếu không có xe phía trước; khi cần thiết phải chuyển làn đường, người lái xe phải quan sát xe phía trước để bảo đảm an toàn.', '2. Phải cho xe đi trong một làn đường và chỉ\neđược chuyển làn đường ở những nơi cho phép;\nkhi chuyển làn phải có tín hiệu báo trước và\nphải bảo đảm an toàn.', '3. Phải cho xe đi trong một làn đường, khi cần thiểt phải chuyển làn đường, người lái xe phải quan sát xe phía trước để bảo đảm an toàn.', '', 'b', '', '2', '10'),
(96, 'Bạn đang lái xe trong khu vực đô thị từ 22 giờ đến 5 giờ sáng hôm sau và cần vượt một xe khác, bạn cần báo hiệu như thế nào để đảm bảo an toàn giao thông?', '', '1. Phải báo hiệu bằng đèn hoặc còi.', '2. Chỉ được báo hiệu băng còi.', '3. Phải báo hiệu bằng cả còi và đèn.', '4. Chỉ được báo hiệu bằng đèn.', 'd', '', '2', '11'),
(97, 'Khi điều khiển xe chạy trên đường biết có xe sau xin vượt nếu đủ điều kiện an toàn người lái xe phải làm gì?', '', '1 . Tăng tốc độ và ra hiệu cho xe sau vượt, không được gây trở ngại cho xe sau vượt.', '2. Người điều khiển phương tiện phía trước phải giảm tốc độ, đi sát về bên phải của phần\nđường xe chạy cho đến khi xe sau đã vượt qua, không được gây trở ngại cho xe sau vượt.', '3. Cho xe tránh về bên trái mình và ra hiệu cho xe sau vượt; nếu có chướng ngại vật phía trước hoặc thiếu điều kiện an toàn chưa cho vượt được phải ra hiệu cho xe sau biết; cấm gây trở ngại cho xe xin vượt.', '', 'b', '', '2', '10'),
(98, 'Trong khu dân cư, ở nơi nào cho phép người lái xe, người điều khiển xe máy chuyên dùng được quay đầu xe?', '', '1. Ở nơi đường giao nhau và nơi có biển báo cho phép quay đầu xe.', '2. Ở nơi có đường rộng để cho các loại xe chạy một chiều.', '3. Ở bất kỳ nơi nào.', '', 'a', '', '2', '9'),
(99, 'Khi muốn chuyển hướng, người lái xe phải thực hiện như thế nào để đảm bảo an toàn giao thông?', '', '1. Quan sát gương, ra tín hiệu chuyển hướng, quan sát an toàn và chuyển hướng.', '2. Quan sát gương, giảm tốc độ, ra tín hiệu chuyẽn hướng, quan sát an toàn và chuyển hướng.', '3. Quan sát gương, tăng tốc độ, ra tín hiệu và chuyển hướng.', '', 'b', '', '2', '6'),
(100, 'Khi lùi xe người lái xe phải làm gì để bảo đảm an toàn?', '', '1. Quan sát phía trước và cho lùi xe ở tốc độ chậm.', '2. Lợi dụng nơi đường giao nhau đủ chiều rộng để lùi.', '3. Phải quan sát phía sau, có tín hiệu cần thiết và chỉ nào thấy không nguy hiểm mới được lùi.\n', '', 'c', '', '2', '7'),
(101, 'Khi bạn nhìn thấy đèn phía sau xe ô tô có màu sáng trắng, ô tô đó đang trong tình trạng như thế', '', '1 . Đang phanh.', '2. Đang bật đèn sương mù.', '3. Đang chuẩn bị lùi hoặc đang lùi.', '4. Đang bị hỏng động cơ.', 'c', '', '2', '6'),
(102, 'Khi tránh xe đi ngược chiều, các xe phải nhường  đường như thế nào là đúng quy tắc giao thông?', '', '1. Nơi đường hẹp chỉ đủ cho một xe chạy và có chỗ tránh xe thì xe nào ở gần chỗ tránh hơn phải vào vị trí tránh, nhường đường cho xe kia đi.', '2. Xe xuống dốc phải nhường đường cho xe đang lên dốc; xe nào có chướng ngại vật phía trước phải nhường đường cho xe không có chướng ngại vật đi trước.', '3. Xe lên dốc phải nhường đường cho xe xuống dốc; xe nào không có chướng ngại vật phía trước phải nhường đường cho xe có chướng ngại vật đi trước', '4. cả ý 1 và ý 2.', 'd', '', '2', '5'),
(103, 'Bạn đang lái xe trên đường hẹp, xuống dốc và gặp một xe đang đi lên dốc, bạn cần làm gì?', '', '1. Tiếp tục đi vì xe lên dốc phải nhường đường cho mình.', '2. Nhường đường cho xe lên dốc.', '3. Chỉ nhường đường khi xe lên dốc nháy đèn.', '', 'b', '', '1', '10'),
(104, 'Tại nơi đường giao nhau, người lái xe đang đi trên đường không ưu tiên phải nhường đường như thế nào là đúng quy tắc giao thông?', '', '1. Nhường đường cho xe đi ở bên phải mình tới.', '2. Nhường đường cho xe đi ở bên trái mình tới.', '3. Nhường đường cho xe đi trên đường ưu tiên hoặc đường chính từ bất kỳ hướng nào tới.', '', 'c', '', '1', '8'),
(105, 'Tại nơi đường giao nhau không có báo hiệu đi theo vòng xuyến, người điều khiển phương tiện phải nhường đường như thế nào là đúng quy tắc giao thông?', '', '1. Phải nhường đường cho xe đi đến từ bên phải.', '2. Xe báo hiệu xin đường trước xe đó được đi trước.', '3. Phải nhường đường cho xe đi đến từ bên trái.', '', 'a', '', '1', '1'),
(106, 'Tại nơi đường bộ giao nhau cùng mức với đường sắt chỉ có đèn tín hiệu hoặc chuông báo hiệu, khi đèn tín hiệu màu đỏ đã bật sáng hoặc có tiếng chuông báo hiệu, người tham gia giao thông phải dừng lại ngay và giữ khoảng cách tối thiểu bao nhiêu mét tính từ ray gần nhất?', '', '1. 1.5mét.', '2. 3 mét.', '3.4 mét.', '', 'a', '', '1', '11'),
(107, 'Trên đường cao tốc, người lái xe phải dừng xe, đỗ xe như thế nào để đảm bảo an toàn giao thông?', '', '1. Không được dừng xe, đỗ xe hoặc chỉ được dừng xe, đỗ xe ở nơi đường rộng, nếu dừng, đỗ xe ở nơi đường hẹp phải sử dụng còi báo hiệu để người lái xe khác biết.', '2. Chỉ được dừng xe, đỗ xe ở nơi quy định, trường hợp buộc phải dừng xe, đỗ xe không đúng nơi quy định thì người lái xe phải đưa xe ra khỏi phần đường xe chạy, nếu không thể được thì phải báo hiệu để người lái xe khác biết.', '3. Chỉ được dừng xe, đỗ xe ở nơi đường rộng; trường hợp dừng xe, đỗ xe tại nơi đường hẹp phải đặt các chướng ngại vật trên đường để yêu cầu người lái xe khác giảm tốc độ để bảo đảm an toan', '', 'b', '', '1', '4'),
(108, 'Những trường hợp nào ghi ở dưới đây không được đi vào đường cao tốc trừ người, phương tiện, thiết bị phục vụ cho việc quản lý, bảo trì đường cao tốc?', '', '1. Người đi bộ, xe thô sơ, xe gắn máy, xe mô tô và máy kéo; xe máy chuyên dùng có tốc độ thiết kế nhỏ hơn 70km/h.', '2. Xe mô tô và xe máy chuyên dùng có tốc độ thiết kế lớn hơn 70km/h.', '3. Người đi bộ, xe thô sơ, xe gắn máy và xe ô tô.', '', 'a', '', '1', '8'),
(109, 'Người điều khiển phương tiện tham gia giao thông trong hầm đường bộ ngoài việc phải tuân thủ các quy tắc giao thông còn phải thực hiện những quy định nào dưới đây?', '', '1. Xe cơ giới, xe máy chuyên dùng phải bật đèn; xe thô sơ phải bật đèn hoặc có vật phát sáng báo hiệu; chỉ được dừng xe, đỗ xe ở nơi quy định.', '2. Xe cơ giới phải bật đèn ngay cả khi đường hầm sáng; phải cho xe chạy trên một làn đường và chỉ chuyển làn ở nơi được phép; được quay đầu xe, lùi xe khi cần thiết.', '3. Xe máy chuyên dùng phải bật đèn ngay cả khi đường hầm sáng; phải cho xe chạy trên một làn đường và chỉ chuyển làn ở nơi được phép; được quay đầu xe, lùi xe khi cần thiết.', '', 'a', '', '1', '9'),
(110, 'Người điều khiển xe mô tô hai bánh, xe gắn máy được phép chở tối đa 2 người trong những trường hợp nào?', '', '1. Chở người bệnh đi cấp cứu; trẻ em dưới 14 tuổi.', '2. Áp giải người có hành vi vi phạm pháp luật.', '3. cả ý 1và ý 2.', '', 'c', '', '1', '10'),
(111, 'Người điều khiển xe mô tô hai bánh, xe gắn máy không được thực hiện những hành vi nào dưới đây?', '', '1. Đi vào phần đường dành cho người đi bộ và phương tiện khác; sử dụng ô, điện thoại di động, thiết bị âm thanh (trừ thiết bị trợ thính), đi xe dàn hàng ngang.', '2. Chở 02 người; trong đó, có người bệnh đi cấp cứu hoặc trẻ em dưới 14 tuổi hoặc áp giải người có hành vi vi phạm pháp luật.', '3. Điều khiển phương tiện tham gia giao thông trên đường tỉnh lộ hoặc quốc lộ.', '', 'a', '', '1', '8'),
(112, 'Người lái xe phải giảm tốc độ thấp hơn tốc độ tối đa cho phép (có thể dừng lại một cách an toàn) trong trường hợp nào dưới đây?', '', '1. Khi có báo hiệu cảnh báo nguy hiểm hoặc có chướng ngại vật trên đường; khi chuyển hướng xe chạy hoặc tầm nhìn bị hạn chếÿ khi qua nơi đường giao nhau, nơi đường bộ giao nhau với đường sắt; đường vòng; đường có địa hình quanh co, đèo dốc.', '2. Khi qua cầu, cống hẹp; khi lên gần đỉnh dốc, khi xuống dốc, khi qua trường học, khu đông dân cư, khu vực đang thi công trên đường bộ; hiện trường xảy ra tai nạn giao thông.', '3. Khi điều khiển xe vượt xe khác trên đường quốc lộ, đường cao tốc.', '4.cả ý 1và ý 2.', 'd', '', '1', '4'),
(113, 'Tại ngã ba hoặc ngã tư không có đảo an toàn, người lái xe phải nhường đường như thế nào là đúng trong các trường hợp dưới đây?', '', '1. Nhường đường cho người đi bộ đang đi trên phần đường dành cho người đi bộ sang đường; nhường đường cho xe đi trên đường ưu tiên, đường chính từ bất kỳ hướng nào tới; nhường đường cho xe ưu tiên, xe đi từ bên phải đến.', '2. Nhường đường cho người đi bộ đang đứng chờ đi qua phần đường dành cho người đi bộ sang đường; nhường đường cho xe đi trên đường ngược chiều, đường nhánh từ bất kỳ hướng nào tới; nhường đường cho xe đi từ bên trái đến.', '3. Không phải nhường đường.', '', 'a', '', '1', '1'),
(114, 'Khi điều khiển xe cơ giới người lái xe phải bật đèn tín hiệu rẽ nào trong các trường hợp nào sau đây?', '', '1. Khi cho xe chạy thẳng.', '2. Trước khi thay đổi làn đường.', '3. Sau khi thay đổi làn đường.', '', 'b', '', '1', '9'),
(115, 'Trên đoạn đường hai chiều không có giải phân cách giữa, người lái xe không được vượt xe khác trong các trường hợp nào dưới đây?', '', '1 . Xe bị vượt bất ngờ tăng tốc độ và cố tình không nhường đường.', '2. Xe bị vượt giảm tốc độ và nhường đường.', '3. Phát hiện có xe đi ngược chiều.', '4.Cả ý 1 và ý 3.', 'd', '', '1', '9'),
(116, 'Người lái xe mô tô xử lý như thế nào khi cho xe mô tô phía sau vượt?', '', '1. Nếu đủ điều kiện an toàn, người lái xe phải giảm tốc độ, đi sát về bên phải của phần đường xe chạy cho đến khi xe sau đã vượt qua, không được gây trở ngại đối với xe xin vượt.', '2. Lái xe vào lề đường bên trái và giảm tốc độ để xe phía sau vượt qua, không được gây trở ngại đối với xe xin vượt.', '3. Nếu đủ điều kiện an toàn, người lái xe phải tăng tốc độ, đi sát về bên phải của phần đường xe chạy cho đến khi xe sau đã vượt qua.', '', 'a', '', '3', '6'),
(117, 'Khi xe ô tô bị hỏng tại vị trí giao nhau giữa đường bộ và đường sắt, người lái xe xử lý như thế nào?', '', '1. Nhanh chóng đặt báo hiệu trên đường sắt cách tối thiểu 500 mét về hai phía để báo cho người điều khiển phương tiện đường sắt và tìrn cách báo cho người quản lý đường sắt, nhà ga nơi gần nhất, đồng thời phải bằng mọi biện pháp nhanh chóng đưa ô tô hỏng ra khỏi phạm vi an toàn đường sắt.', '2. Nhanh chóng đặt biển cảnh báo nguy hiểm tại vị trí xe ô tô bị hỏng để đoàn tàu dừng lại.', '3. Liên hệ ngay với đơn vị cứu hộ để đưa ô tô hỏng ra khỏi phạm vi an toàn đường sắt.', '', 'a', '', '3', '8'),
(118, 'Trong các trường hợp dưới đây, để đảm bảo an toàn khi tham gia giao thông, người lái xe mô tô cần thực hiện như thế nào?', '', '1. Phải đội mũ bảo hiểm đạt chuẩn, có cài quai đúng quy cách, mặc quần áo gọn gàng; không sử dụng ô, điện thoại di động, thiết bị âm thanh (trừ thiết bị trợ thính).', '2. Phải đội mũ bảo hiểm khi trời mưa gió hoặc trời quá nắng; có thể sử dụng ô, điện thoại di động, thiết bị âm thanh nhưng phải đảm bảo an toàn.', '3. Phải đội mũ bảo hiểm khi cảm thấy mất an toàn giao thông hoặc khi chuẩn bị di chuyển quãng đường xa.', '', 'a', '', '3', '9'),
(119, 'Đường bộ trong khu vực đông dân cư gồm những đoạn đường nào dưới đây?', '', '1. Là đoạn đường năm trong khu công nghiệp có đông người và phương tiện tham gia giao thông và được xác định cụ thể băng biển chỉ dẫn địa giới.', '2. Là đoạn đường bộ năm trong khu vực nội thành phố, nội thị xã, nội thị trấn và những đoạn đường có dân cư sinh sống sát dọc theo đường, có các hoạt động có thể ảnh hưởng đến giao thông đường bộ và được xác định băng biển báo là đường qua khu đông dân cư.', '3. Là đoạn đường năm ngoài khu vực nội thành phố, nội thị xã có đông người và phương tiện tham gia giao thông và được xác định cụ thể bằng biển chỉ dẫn địa giới.', '', 'b', '', '3', '5'),
(120, 'Tốc độ tối đa cho phép đối với xe máy chuyên dùng, xe gắn máy (kể cả xe máy điện) và các loại xe tương tự trên đường bộ (trừ đường cao tốc) không được vượt quá bao nhiêu km/h?', '', '1. 50 km/h.', '2. 40km/h.', '3. 60 km/h.', '', 'b', '', '3', '2'),
(121, 'Trên đường bộ (trừ đường cao tốc) trong khu vực đông dân cư, đường đôi có dải phân cách giữa, xe mô tô hai bánh, ô tô chở người đến 30 chỗ tham gia giao thông với tốc độ tối đa cho phép là bao nhiêu?', '', '1. 60km/h.', '2. 50 km/h.', '3. 40km/h.', '', 'a', '', '3', '5'),
(122, 'Trên đường bộ (trừ đường cao tốc) trong khu vực đông dân cư, đường hai chiều không có dải phân cách giữa, xe mô tô hai bánh, ô tô chở người đến 30 chỗ tham gia giao thông với tốc độ tối đa cho phép là bao nhiêu?', '', '1. 60 km/h.', '2.50km/h.', '3. 40 km/h.', '', 'b', '', '3', '5'),
(123, 'Trên đường bộ (trừ đường cao tốc) trong khu vực đông dân cư, đường hai chiều hoặc đường một chiều có một làn xe cơ giới, loại xe nào tham gia giao thông với tốc độ tối đa cho phép là 50 km/h?', '', '1. Ô tô con, ô tô tải, ô tô chở người trên 30 chỗ.', '2. Xe găn máy, xe máy chuyên dùng.', '3. Cả ý 1 và ý 2.', '', 'a', '', '3', '4'),
(124, 'Trên đường bộ (trừ đường cao tốc) ngoài khu vực đông dân cư, đường đôi có dải phân cách giữa, loại xe nào tham gia giao thông với tốc độ tối đa cho phép là 90 km/h?', '', '1 . Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3.500 kg.', '2. Xe ô tô con, xe ô tô chở người đến 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải đến 3.500kg', '3. Ô tô buýt, ô tô đầu kéo kéo sơ mi rơ moóc, ô tô chuyên dùng, xe mô tô.', '', 'b', '', '3', '7'),
(125, 'Trên đường bộ ngoài khu vực đông dân cư, đường đôi có dải phân cách giữa (trừ đường cao tốc), loại xe nào tham gia giao thông với tốc độ tối đa cho phép là 80 km/h?', '', '1.Ô tô buýt; ô tô đầu kéo kéo sơ mi rơ moóc; ô tô chuyên dùng; xe mô tô.', '2. Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3.500 kg (trừ ô tô xi téc).', '3. Ô tô kéo rơ moóc, ô tô kéo xe khác, xe gắn máy.', '4. Xe ô tô con, xe ô tô chở người đến 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải đến 3.500kg.', 'b', '', '3', '11'),
(126, 'Trên đường bộ ngoà u vực đông dân cư, đườnghai chiều không có dải phân cách giữa; đường một chiều có một làn xe cơ giới (trừ đường cao tốc), loại xe nào tham gia giao thông với tốc độ tối đa cho phép là 80 km/h?', '', '1. Ô tô kéo rơ moóc, ô tô kéo xe khác, xe gắn máy.', '2. Ô tô chở người trên 30 chỗ (trừ ô tô buýt), Ô tô tải có trọng tải trên 3.500 kg.', '3. Xe ô tô con, xe ô tô chở người đến 30 chỗ (trừ xe buýt), ô tô tải có trọng tải nhỏ hơn hoặc bằng 3.500 kg.', '', 'c', '', '3', '3'),
(127, 'Trên đường bộ ngoài khu vực đông dân cư, đường hai chiều không có dải phân cách giữa; đường một chiều có một làn xe cơ giới (trừ đường cao tốc), loại xe nào tham gia giao thông với tốc độ tối đa cho phép là 70 km/h?', '', '1. Ô tô kéo rơ moóc, ô tô kéo xe khác, xe gắn máy.', '2, Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3.500 kg (trừ ô tô xi téc).', '3. Xe ô tô con, xe ô tô chở người đến 30 chỗ (trừ xe buýt), ô tô tải có trọng tải nhỏ hơn hoặc băng 3.500kg.', '', 'b', '', '3', '1');
INSERT INTO `cauhoi_dapan` (`cau`, `nd_cauhoi`, `hinh_cauhoi`, `a`, `b`, `c`, `d`, `caudung`, `caudiemliet`, `loai_CH`, `BD_S`) VALUES
(128, 'Trên đường bộ ngoài khu vực đông dân cư, đường hai chiều không có dải phân cách giữa; đường một chiều có một làn xe cơ giới (trừ đường cao tốc), loại xe nào tham gia giao thông với tốc độ tối đa cho phép là 60 km/h?', '', '1. Ô tô kéo rơ moóc, ô tô kéo xe khác, xe gắn máy.', '2. Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3.500 kg.', '3. Ô tô buýt, ô tô đầu kéo kéo sơ mi rơ moóc, xe mô tô, ô tô chuyên dùng (trừ ô tô trộn vữa, ô tô trộn bê tông).', '', 'c', '', '3', '4'),
(129, 'Trên đường bộ ngoài khu vực đông dân cư, đường hai chiều không có dải phân cách giữa; đường một chiều có một làn xe cơ giới (trừ đường cao tốc), loại xe nào tham gia giao thông với tốc độ tối đa cho phép là 50 km/h?', '', '1. Ô tô kéo rơ moóc, ô tô kéo xe khác, xe gắn máy.', '2. Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3.500 kg.', '3. Ô tô kéo rơ moóc, ô tô kéo xe khác, ô tô trộn vữa, ô tô trộn bê tông, ô tô xi téc.', '', 'c', '', '3', '1'),
(130, 'Khi tham gia giao thông trên đoạn đường không có biển báo “Cự ly tối thiểu giữa hai xe”, với điều kiện mặt đường khô ráo, xe cơ giới đang chạy với tốc độ từ trên 60 km/h đến 80 km/h, người lái xe phải duy trì khoảng cách an toàn với xe đang chạy phía trước tối thiểu là bao nhiêu?', '', '1.35m.', '2.55m.', '3.70m.', '', 'b', '', '1', '4'),
(131, 'Khi tham gia giao thông trên đoạn đường không có biển báo “Cự ly tối thiểu giữa hai xe”, với điều kiện mặt đường khô ráo, xe cơ giới đang chạy với tốc độ từ trên 80 km/h đến 100 km/h, người lái xe phải duy trì khoảng cách an toàn với xe đang chạy phía trước tối thiểu là bao nhiêu?', '', '1.35m.', '2.55m.', '3. 70m', '', 'c', '', '1', '7'),
(132, 'Khi tham gia giao thông trên đoạn đường không có biển báo “Cự ly tối thiểu giữa hai xe”, với điều kiện mặt đường khô ráo, xe cơ giới đang chạy với tốc độ từ trên 100 km/h đến dưới 120 km/h, người lái xe phải duy trì khoảng cách an toàn với xe đang chạy phía trước tối thiểu là bao nhiêu?', '', '1.55m.', '2.70m.', '3. 100m', '', 'c', '', '1', '3'),
(133, 'Khi điều khiển xe chạy với tốc độ dưới 60 km/h, người lái xe phải chủ động điều khiển xe như thế nào để đảm bảo an toàn giao thông?', '', '1. Chủ động giữ khoảng cách an toàn phù hợp với xe chạy liền trước xe của mình.', '2. Đảm bảo khoảng cách an toàn theo mật độ phương tiện, tình hình giao thông thực tế.', '3.cả ý 1 và ý 2', '', 'c', '', '1', '3'),
(134, 'Người lái xe phải giảm tốc độ thấp hơn tốc độ đến mức cần thiết, chú ý quan sát và chuẩn bị sẵn sàng những tình huống có thế xảy ra để phòng ngừa tai nạn trong các trường hợp nào dưới đây?', '', '1. Gặp biển báo nguy hiểm trên đường.', '2. Gặp biển chỉ dẫn trên đường.', '3. Gặp biển báo hết mọi lệnh cấm.', '4. Gặp biển báo hết hạn chế tốc độ tối đa cho phép.', 'a', '', '1', '6'),
(135, 'Người lái xe phải giảm tốc độ, đi sát về bên phải của phần đường xe chạy trong các trường hợp nào dưới đây?', '', '1. Khi vượt xe khác.', '2. Khi vượt xe khác tại đoạn đường được phép vượt.', '3. Khi xe sau xin vượt và đảm bảo an toàn.', '4. Khi xe sau có tín hiệu vượt bên phải.', 'c', '', '1', '7'),
(136, 'Các phương tiện tham gia giao thông đường bộ (kể cả những xe có quyền ưu tiên) đều phải dừng lại bên phải đường của mình và trước vạch “dừng xe” tại các điểm giao cắt giữa đường bộ và đường sắt khi có báo hiệu dừng nào dưới đây?', '', '1 . Hiệu lệnh của nhân viên gác chắn.', '2. Đèn đỏ sáng nháy, cờ đỏ, biển đỏ.', '3. Gòi, chuông kêu, chắn đã đóng.', '4. Tất cả các ý trên.', 'd', '', '1', '1'),
(137, 'Tác dụng của mũ bảo hiểm đối với người ngồi trên xe mô tô hai bánh trong trường hợp xảy ra tai nạn giao thông là gì?', '', '1. Để làm đẹp.', '2. Để tránh mưa nắng', '3. Để giảm thiểu chấn thương vùng đầu.', '4. Để các loại phương tiện khác dễ quan sát.', 'c', '', '1', '8'),
(138, 'Người lái xe phải nhanh chóng giảm tốc độ, tránh hoặc dừng lại sát lê đường bên phải để nhường đường cho các xe nào nêu dưới đây?', '', '1. Xe chữa cháy, xe quân sự, xe công an, xe cứu thương, xe hộ đê sau khi thực hiện nhiệm vụ khẩn cấp, không có tín hiệu còi, cờ, đèn theo quy định của pháp luật.', '2. Xe chữa cháy, xe quân sự, xe công an, xe cứu thương, xe hộ đê đi làm nhiệm vụ khẩn cấp có tín hiệu còi, cờ, đèn theo quy định của pháp luật.', '3. Xe ô tô, xe máy, đoàn xe đang diễu hành có tố chức có báo tín hiệu xin vượt bằng còi và đèn.', '', 'b', '', '1', '2'),
(139, 'Tại nơi đường giao nhau, người lái xe đang đi trên đường không ưu tiên phải xử lý như thế nào là đúng quy tắc giao thông?', '', '1. Tăng tốc độ qua đường giao nhau để đi trước xe đi trên đường ưu tiên.', '2. Giảm tốc độ qua đường giao nhau để đi trước xe đi trên đường ưu tiên.', '3. Nhường đường cho xe đi trên đường ưu tiên từ bất kỳ hướng nào tới.', '', 'c', '', '1', '5'),
(140, 'Khi điều khiển xe trên đường vòng, khuất tầm nhìn người lái xe cần phải làm gì để đảm bảo an toàn?', '', '1. Đi đúng làn đường, đúng tốc độ quy định, không được vượt xe khác.', '2. Đi sang làn đường của xe ngược chiều để mở rộng tầm nhìn và vượt xe khác', '3. Cho xe đi sát bên phải làn đường, bật tín hiệu báo hiệu để vượt bên phải xe khác.', '', 'a', '', '1', '4'),
(141, 'Người lái xe phải xử lý như thế nào khi quan sát phía trước thấy người đi bộ đang sang đường tại nơi có vạch đường dành cho người đi bộ để đảm bảo an toàn?', '', '1. Giảm tốc độ, đi từ từ để vượt qua trước người đi bộ.', '2. Giảm tốc độ, có thể dừng lại nếu cần thiết trước vạch dừng xe để nhường đường cho người đi bộ qua đường.', '3. Tăng tốc độ để vượt qua trước người đi bộ.', '', 'b', '', '1', '5'),
(142, 'Tai những đoạn đường không bố trí biển báo hạn chế tốc độ, không bố trí biển báo khoảng cách an toàn tối thiểu giữa hai xe, người điều khiển phương; tiện tham gia giao thông phải thực hiện quy định nào dưới đây để đảm bảo an toàn giao thông?', '', '1. Người điều khiển phương tiện tham gia giao thông không hạn chế tốc độ và khoảng cách an toàn tối thiểu giữa hai xe.', '2. Người điều khiển phương tiện tham gia giao thông không hạn chế tốc độ và khoảng cách an toàn tối thiểu giữa hai xe vào ban đêm.', '3. Người điều khiển phương tiện tham gia giao thông phải nghiêm chỉnh chấp hành quy định về tốc độ, khoảng cách an toàn tối thiểu giữa hai xe.', '', 'c', '', '1', '1'),
(143, 'Khi tham gia giao thông trên đường cao tốc, người lái xe, người điều khiển xe máy chuyên dùng phải thực hiện các quy định như thế nào là đúng quy tắc giao thông?', '', '1. Tuân thủ tốc độ tối đa, tốc độ tối thiểu ghi trên biển báo hiệu đường bộ, sơn kẻ mặt đường trên các làn xe.', '2. Tuân thủ tốc độ tối đa, tốc độ tối thiểu ghi trên biển báo hiệu đường bộ, sơn kẻ mặt đường trên các làn xe chỉ vào ban ngày.', '3. Tuân thủ tốc độ tối đa, tốc độ tối thiểu ghi trên biển báo hiệu đường bộ, sơn kẻ mặt đường trên các làn xe chỉ vào ban đêm.', '', 'a', '', '1', '10'),
(144, 'Theo Luật Giao thông đường bộ, tín hiệu đèn giao thông gồm 3 màu nào dưới đây?', '', '1. Đỏ - Vàng - Xanh', '2. Cam - Vàng - Xanh.', '3. Vàng - Xanh dương - Xanh lá.', '4. Đỏ - Cam - Xanh.', 'a', '', '1', '11'),
(145, 'Tại nơi đường giao nhau, khi đèn điều khiển giao thông có tín hiệu màu vàng, người điều khiển phương tiện giao thông phải chấp hành như thế nào là đúng quy tắc giao thông?', '', '1. Phải cho xe dừng lại trước vạch dừng, trường hợp đã đi quá vạch dừng hoặc đã quá gần vạch dừng nếu dừng lại thấy nguy hiểm thì được đi tiếp.', '2. Trong trường hợp tín hiệu vàng nhấp nháy là được đi nhưng phải giảm tốc độ, chú ý quan sát nhường đường cho người đi bộ qua đường.', '3. Nhanh chóng tăng tốc độ, vượt qua nút giao và chú ý đảm bảo an toàn.', '4. Cả ý 1 và ý 2.', 'd', '', '1', '11'),
(146, 'Để báo hiệu cho xe phía trước biết xe mô tô của bạn muốn vượt, bạn phải có tín hiệu như thế nào dưới đây?', '', '1. Ra hiệu bằng tay rồi cho xe vượt qua.', '2. Tăng ga mạnh để gây sự chú ý rồi cho xe vượt qua.', '3. Bạn phải có báo hiệu bằng đèn hoặc còi.', '', 'c', '', '1', '7'),
(147, 'Khi tham gia giao thông trên đường cao tốc, người điều khiển phương tiện cơ giới có được dừng, đỗ xe trên phần đường xe chạy hay không?', '', '1. Được dừng, đỗ.', '2. Không được dừng, đỗ.', '3. Được dừng, đỗ nhưng phải đảm bảo an toàn', '', 'b', '', '1', '10'),
(148, 'Khi người lái xe ô tô dừng, đỗ sát theo lề đường, hè phố phía bên phải theo chiều đi của mình, bánh xe gần nhất không được cách xa lê đường, hè phố quá bao nhiêu mét trong các trường hợp dưới đây để không gây cản trở, nguy hiểm cho giao thông?', '', '1. 0,25 mét.', '2. 0,3 mét.', '3. 0,4 mét.', '4. 0,5 mét.', 'a', '', '2', '1'),
(149, 'Khi dừng, đỗ xe trên đường phố hẹp, người lái xe ô tô phải dừng, đỗ xe ở vị trí cách xe ô tô đang đỗ bên kia đường khoảng cách tối thiểu là bao nhiêu mét trong các trường hợp dưới đây để đảm bảo an toàn giao thông?', '', '1. 5 mét.', '2. 10 mét.', '3.15 mét.', '4. 20 mét.', 'a', '', '2', '11'),
(150, 'Trên đường bộ, người lái xe ô tô có được phép dừng xe, đỗ xe song song với một xe khác đang dừng, đỗ hay không?', '', '1 . Được phép.', '2. Không được phép.', '3. Chỉ được phép dừng, đỗ khi đường vắng.', '', 'b', '', '2', '5'),
(151, 'Người điều khiển xe mô tô phải giảm tốc độ và hết sức thận trọng khi qua những đoạn đường nào dưới đây?', '', '1. Đường ướt, đường có sỏi cát trên nền đường.', '2. Đường hẹp có nhiều điểm giao cắt từ hai phía.', '3. Đường đèo dốc, vòng liên tục', '4. Tất cả các ý nêu trên.', 'd', '', '1', '9'),
(152, 'Khi gặp xe buýt đang dừng đón, trả khách, người điều khiển xe mô tô phải xử lý như thế nào dưới đây để đảm bảo an toàn giao thông?', '', '1 . Tăng tốc độ để nhanh chóng vượt qua bến đỗ.', '2. Giảm tốc độ đến mức an toàn có thể và quan sát người qua đường và từ từ vượt qua xe buýt.', '3. Yêu cầu phải dừng lại phía sau xe buýt chờ xe rời bến mới đi tiếp.', '', 'b', '', '2', '6'),
(153, 'Biển nào cấm các loại xe cơ giới đi vào, trừ xe gắn máy, mô tô hai bánh và các loại xe ưu tiên theo luật định?', 'https://lh3.googleusercontent.com/sq2FXTrepdwqp2RnslKcedddWBr4nyEsjHcLmDN32K5o45JPapZV4yxTwwgelrgOZQ5HmA=s170', '1. Biển1.', '2. Biển 2.', '3. Biển 1 và 3.', '4. Cả ba biển.', 'a', '', '4', '4'),
(154, 'Biển nào cấm ô tô tải?', 'https://lh3.googleusercontent.com/Pr81s-UUQ4iVFSGMl8A24i6ly0Ox4mZD0m632HOLexSUk-9k8AYxekiKSuI1H28-gbp5Sw=s170', '1. Cả ba biển.', '2. Biển 2 và 3.', '3. Biển 1 và 3.', '4. Biển 1 và 2.', 'd', '', '4', '3'),
(155, 'Biển nào cấm máy kéo?', 'https://lh3.googleusercontent.com/hKUtqrRxo_ojKutmsM00Vetu9EZYgt8XK3Roi4ck6Hyv_46PWKdjWH7aiNvX4YQ_YpFb=s170', '1.Biển 1.', '2. Biển 2 và 3.', '3. Biển 1 và 3.', '4. Cả ba biển.', 'b', '', '4', '8'),
(156, 'Biển nào báo hiệu cấm xe mô tô ba bánh đi vào?', 'https://lh3.googleusercontent.com/ePywmUGSP-vRyQWMTk66l3igs65Zq-s8b3adcKkNN2-hzEJD1YDfi_dy0sM-MdJtzSSrF-c=s170', '1.Biển 1 và 2.', '2.Biển1 và 3.', '3. Biển 2 và 3.', '', 'a', '', '4', '7'),
